// src/components/KioskForm.js
import React, { useState, useEffect } from 'react';
//import emailjs from 'emailjs-com';

const KioskForm = () => {
    const [name, setName] = useState('');
    const [company, setCompany] = useState('');
    const [mobile, setMobile] = useState('');
    const [people, setPeople] = useState([]); // State for names
    const [selectedPerson, setSelectedPerson] = useState(''); // State for selected person



    useEffect(() => {
        // Fetch names from the JSON file
        fetch('/names.json')
            .then((response) => response.json())
            .then((data) => setPeople(data))
            .catch((error) => console.error('Error fetching names:', error));
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        const templateParams = {
            name,
            company,
            mobile,
        };
/* 
        emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', templateParams, 'YOUR_USER_ID')
            .then((response) => {
                console.log('Email sent successfully!', response.status, response.text);
            })
            .catch((err) => {
                console.error('Failed to send email. Error: ', err);
            }); */
    };

    return (
        <form onSubmit={handleSubmit} className="bg-white bg-opacity-70 backdrop-blur-lg p-8 rounded-lg shadow-md w-3/4 md:w-1/2 lg:w-1/3">
            <h2 className="text-2xl font-bold mb-4">Visitor Registration</h2>
            <div className="mb-4">
                <select
                    id="person"
                    value={selectedPerson}
                    onChange={(e) => setSelectedPerson(e.target.value)}
                    className="w-full p-5 border border-gray-300 rounded-lg"
                    required
                >
                    <option value="" disabled>Select the person you are visiting</option>
                    {people.map((person, index) => (
                        <option key={index} value={person}>{person}</option>
                    ))}
                </select>
            </div>
            <div className="mb-4">
                <input
                    type="text"
                    placeholder="Your name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                    className="w-full p-5 border border-gray-300 rounded"
                />
            </div>
            <div className="mb-4">
                <input
                    type="text"
                    placeholder="Company you are from"
                    value={company}
                    onChange={(e) => setCompany(e.target.value)}
                    required
                    className="w-full p-5 border border-gray-300 rounded"
                />
            </div>
            <div className="mb-4">
                <input
                    type="tel"
                    placeholder="Your mobile number"
                    value={mobile}
                    onChange={(e) => setMobile(e.target.value)}
                    required
                    className="w-full p-5 border border-gray-300 rounded"
                />
            </div>
            <button
                type="submit"
                className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition"
            >
                Submit
            </button>
        </form>
    );
};

export default KioskForm;
