import React, {useState,useEffect} from 'react';
import KioskForm from './KioskForm';
import './App.css';

const App = () => {

  const [bgVideo, setBGVideo] = useState('/vidoes/bg1.mp4');

  useEffect(() => {
      // Fetch names from the JSON file
      fetch('/videos.json')
          .then((response) => response.json())
          .then((data) => setBGVideo(data[Math.floor(Math.random() * data.length)]))
          .catch((error) => console.error('Error fetching videos:', error));
  }, [setBGVideo]);

  return (
    <div className="relative w-full h-screen overflow-hidden">
      <video
        className=" object-cover"
        autoPlay
        loop
        muted
        playsInline
      >
      <source src="/videos/bg3.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>
      <div className="relative z-10 flex justify-center items-center h-full">
        {console.log(bgVideo)}
        <KioskForm />
      </div>
    </div>
  );
};

export default App;
